function makeCoffee(e="Coffee"){alert(`Here is your ${e}: â˜•`)}function loadCoffee(){let e=new XMLHttpRequest;e.onreadystatechange=function(){if(4!=e.readyState||200!=e.status||""==e.responseText)return;e.responseText.split("\n").forEach((e=>{let t=document.createElement("button");t.innerHTML=e,t.onclick=()=>{makeCoffee(e)},document.body.appendChild(t)}))},e.open("GET","coffee.txt"),e.send()}window.addEventListener("load",loadCoffee);